import './App.css';
import React, { useEffect, useState } from 'react';
import { appleButton, spotifyButton } from './styles';
//import SpotifyWebApi from 'spotify-web-api-js';
import { getSpotifyTokenFromUrl, getLikedSongs, handleSpotifyAuth } from './spotifyApi';

function App() {
  const [isSpotifyLoggedIn, setIsSpotifyLoggedIn] = useState(false);
  const [spotifyToken, setSpotifyToken] = useState(false)

  useEffect( () =>{
    const token = getSpotifyTokenFromUrl();
    if (token){
      setSpotifyToken(token)
      setIsSpotifyLoggedIn(true);
      console.log(token)
    }
  }, []
  );


  return (
    <div className='App'>
      <div style={
        { display: 'flex', 
        flexDirection: 'column', 
        height: '100vh', 
        justifyContent: 'center', 
        alignItems: 'center' 
        }}>

     <button style ={spotifyButton} onClick = {handleSpotifyAuth}>
      spotifyAuth
     </button>
     {isSpotifyLoggedIn && (
        <div style={{ position: 'absolute', bottom: '0', width: '100%' }}>
          <button style={appleButton} onClick = {getLikedSongs}>get liked songs</button>
        </div>
      )}



     </div>
    </div>
  )
      };


export default App;
